import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Home = () => {
    const [data, setData] = useState([]);

    useEffect(() => {
        fetch('http://localhost:8000/api/robots/')
            .then((response) => response.json())
            .then((data) => setData(data.data))
    }, [])

    return (
        <ProductContainer>
            <ProductsSection>
                <h2>Trending Robots</h2>
                <ul>
                    {data.map((item) => {
                        return <li key={Math.random().toString(36).substr(2, 9)}>
                            <Card>
                                <Photo src={item.image} alt={item.name} />
                                <ItemInfo>
                                    <Name>{item.name}</Name>
                                    Price:<Price> {Intl.NumberFormat('th-TH', { style: 'currency', currency: 'THB' }).format(item.price)}</Price>
                                    In-Stock:<Stock> {item.stock}</Stock>
                                    Created Date:<Date> {item.createdAt.substr(0, 9).split('-').reverse().join('-')}</Date>
                                    Material:<Material> {item.material}</Material>
                                    <AddToCart type="button">Add to Cart</AddToCart>
                                </ItemInfo>
                            </Card></li>
                    })}
                </ul>

            </ProductsSection>

            <CartSection>

            </CartSection>

        </ProductContainer>
    )
}
const Photo = styled.img`
    height: 150px;
`;


const ProductContainer = styled.div`
    color: #000;
    display: flex;
    justify-content: space-between;
    background: #fff;
    height: 100%;
`;

const ProductsSection = styled.div`
    width: 80%;
    height: 100vh;
    background: #fff ;
    padding: 1rem 1rem;

    ul {
        margin-top: 25px;
        display: flex;
        justify-content: space-around;
        flex-wrap: wrap;
        gap: 25px;
        list-style-type: none;
    }
`;

const Card = styled.div`
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
    transition: 0.3s;
    width: 300px;
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 20px;
    padding-bottom: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
    }
`;

const ItemInfo = styled.div`
    width: 90%;
    padding: 5px 15px;
    display: flex;
    flex-direction: column;
`;

const Name = styled.h3`
    margin-bottom: 15px;

`;

const Price = styled.h4``;
const Stock = styled.h4``;
const Date = styled.h4``;
const Material = styled.h4``;

const AddToCart = styled.button`
    background: #ff0080;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }
`;


const CartSection = styled.div`
    width: 20%;
    height: 100vh;
    background: #F8F9F9 ;
    border-left: 1px solid #D5D8DC ;
`;


export default Home;
