import React, { useState, useEffect, } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import Navbar from './Navbar';
import SearchBar from './SearchBar';

const Home = () => {
    const limit = 12;
    const [data, setData] = useState([]);
    const [visibleItems, setVisibleItems] = useState(limit);
    const cartData = [{
        "name": "Elwyn Kertzmann",
        "image": "https://robohash.org/Elwyn Kertzmann.png?size=120x120",
        "price": "561.55",
        "stock": 4,
        "createdAt": "2021-03-29T03:35:50.519Z",
        "material": "Wooden"
    },
    {
        "name": "Camden Wunsch",
        "image": "https://robohash.org/Camden Wunsch.png?size=120x120",
        "price": "832.63",
        "stock": 3,
        "createdAt": "2020-06-15T05:18:21.065Z",
        "material": "Concrete"
    },
    {
        "name": "Trystan Hettinger",
        "image": "https://robohash.org/Trystan Hettinger.png?size=120x120",
        "price": "651.89",
        "stock": 8,
        "createdAt": "2021-03-27T19:42:01.800Z",
        "material": "Rubber"
    },
    {
        "name": "Janiya Keeling",
        "image": "https://robohash.org/Janiya Keeling.png?size=120x120",
        "price": "7.05",
        "stock": 10,
        "createdAt": "2020-12-28T03:37:30.528Z",
        "material": "Wooden"
    },
    {
        "name": "Darrell Hoppe",
        "image": "https://robohash.org/Darrell Hoppe.png?size=120x120",
        "price": "747.89",
        "stock": 7,
        "createdAt": "2020-12-19T20:52:56.137Z",
        "material": "Concrete"
    },
    {
        "name": "Yoshiko Witting",
        "image": "https://robohash.org/Yoshiko Witting.png?size=120x120",
        "price": "697.89",
        "stock": 7,
        "createdAt": "2021-04-13T19:50:19.081Z",
        "material": "Plastic"
    },
    {
        "name": "Gerson Moore",
        "image": "https://robohash.org/Gerson Moore.png?size=120x120",
        "price": "819.83",
        "stock": 3,
        "createdAt": "2020-05-20T04:36:00.621Z",
        "material": "Metal"
    },]

    useEffect(() => {
        axios('http://localhost:8000/api/robots/')
            .then(response => {
                setData(response.data.data);
            })
            .catch(error => {
                console.log('Error Fetching Data from the API ' + error);
            })

    }, [])

    const loadMore = () => {
        setVisibleItems(previousVisibleItems => previousVisibleItems + 12)
    }

    return (
        <MainContainer>
            <Navbar />
            <ProductContainer>
                <ProductsSection>
                    <SearchBar />
                    <RobotItems>
                        {data.slice(0, visibleItems).map((item) => {
                            return <li key={Math.random().toString(36).substr(2, 9)}>
                                <Card>
                                    <Photo src={item.image} alt={item.name} />
                                    <Name>{item.name}</Name>
                                    <ItemInfo>
                                        Price:<Price> {Intl.NumberFormat('th-TH', { style: 'currency', currency: 'THB' }).format(item.price)}</Price>
                                        In-Stock:<Stock> {item.stock}</Stock>
                                        Created Date:<Date> {item.createdAt.substr(0, 9).split('-').reverse().join('-')}</Date>
                                        Material:<Material> {item.material}</Material>
                                    </ItemInfo>
                                    <AddToCart disabled={item.stock === 0} >Add to Cart</AddToCart>
                                </Card></li>
                        })}
                    </RobotItems>
                    <ShowMore>
                        <ShowMoreButton onClick={loadMore}>Load more</ShowMoreButton>
                    </ShowMore>
                </ProductsSection>

                <CartSection>
                    <CartHeader>
                        <CartHeading>YOUR CART</CartHeading>
                    </CartHeader>
                    <CartItems>
                        <div>
                            <div>
                                <strong>Name</strong>
                            </div>
                            <div>
                                <strong>Quantity</strong>
                            </div>
                            <div>
                            <strong>Name</strong>
                            </div>
                        </div>
                        {cartData.slice(0, 5).map((anItem) => {
                            return <li key={Math.random().toString(36).substr(2, 9)}>
                                <div>
                                    <div>
                                        {anItem.name}
                                    </div>
                                    <div>
                                        Count
                                    </div>
                                    <div>
                                        {anItem.price}
                                    </div>
                                </div>
                            </li>
                        })}

                    </CartItems>


                </CartSection>
            </ProductContainer>
        </MainContainer>

    )
}
const MainContainer = styled.div`
    color: #fff;
    height: 100%;
    width: 100%;
`;

const Photo = styled.img`
    height: 150px;
    margin-bottom: 10px;
`;


const ProductContainer = styled.div`
    color: #000;
    display: flex;
    justify-content: space-between;
    background: #fff;
    height: auto;
`;

const ProductsSection = styled.div`
    border-right: 1px solid #D5D8DC;  
    width: 80%;
    height: auto;
    background: #fff ;
    padding: 30px 20px;

    h2{
        padding-left: 20px;
        font-weight:900;
    }
`;

const RobotItems = styled.ul`
    margin-top: 40px;
    display: flex;
    justify-content: space-around;
    flex-wrap: wrap;
    gap: 25px;
    list-style-type: none;
`;


const Card = styled.div`
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
    transition: 0.3s;
    width: 300px;
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 20px;
    padding-bottom: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
    }
`;

const ItemInfo = styled.div`
    width: 90%;
    padding: 5px 15px;
    display: grid;
    grid-template-columns: auto auto;
`;

const Name = styled.h3`
    margin-bottom: 15px;
`;

const Price = styled.h4``;
const Stock = styled.h4``;
const Date = styled.h4``;
const Material = styled.h4``;

const AddToCart = styled.button`
    background: #ff0080;
    width: 90%;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }

    &:disabled {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0);
        background: #ABB2B9 ;
    }
`;

const ShowMore = styled.div`
    padding: 30px 5px;

`;

const ShowMoreButton = styled.button`
    background: #ff0080;
    width: 100%;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }

    &:disabled {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0);
        background: #ABB2B9 ;
    }
`;


const CartSection = styled.div`
padding:20px;
    width: 300px;
    height: auto;
    background: #fff ;
`;

const CartHeader = styled.div`
    text-align: center;
    padding-top: 30px;
    padding-bottom: 30px;
    border-bottom: 1px solid black;

`;

const CartHeading = styled.h1`
    font-weight: 800:
`;

const CartItems = styled.ul`

`;




export default Home;
