import React from 'react';
import { useCart } from "react-use-cart";
import styled from 'styled-components';


function Cart() {
    const {
        isEmpty,
        totalItems,
        items,
        cartTotal,
        updateItemQuantity,
        removeItem,
    } = useCart();

    if (isEmpty) return (
        <CartWrapper>
            <CartHeader>
                <CartHeading>Cart</CartHeading>
                <CartMessage>Your cart is empty</CartMessage>
            </CartHeader>
            <CartFooter>
                <p>Total Number of Items: </p>
                <p>Total Amount: </p>
            </CartFooter>
        </CartWrapper>);

    else return (
        <CartWrapper>
            <CartHeading>Cart</CartHeading>
            <CartUL>
                {items.map((item) => (
                    <div>

                    </div>

                ))}
            </CartUL>
            <CartFooter>
                <p>Total Number of Items: {totalItems}</p>
                <p>Total Amount: {cartTotal.toFixed(2)}</p>
            </CartFooter>
        </CartWrapper>
    );
}

const CartWrapper = styled.div`
    padding-top: 50px;
    padding-left: 20px;
    padding-right: 20px;
    text-align: center;
`;

const CartHeader = styled.div`

`;
const CartHeading = styled.h1`
    padding-bottom: 20px;
    border-bottom: 2px solid #D5D8DC;
`;
const CartMessage = styled.p`
    padding-bottom: 20px;
    padding-top: 20px;
    border-bottom: 2px solid #D5D8DC;
`;
const CartFooter = styled.div`
  border-bottom: 2px solid #D5D8DC;
  text-align: left;
  padding-top:20px;
  padding-bottom: 20px;
  p{
    padding: 5px 0;
  }
`;


const CartUL = styled.ul`
  list-style-type: none;
  display: flex;
  flex-direction: column;
  text-align: left;
`;

const CartLI = styled.li`
  padding-top: 10px;
  padding-bottom: 10px;
  padding-left: 10px;
  border-bottom: 0.5px solid #D5D8DC;
`;

const CartBtn = styled.button`
  width: 20px;
  height: 20px;
  margin: 5px;
`;

const CartTitle = styled.div`
  display: grid;
  grid-template-columns: 50% 20% 30%;
`;


export default Cart;
