import React from 'react';
import styled from 'styled-components';

const Cart = () => {
    return (
        <CartSection>
            <CartHeader>
                <CartHeading>YOUR CART</CartHeading>
            </CartHeader>
            <CartItems>
                <CartListTitle>
                    <div>
                        <strong>Name</strong>
                    </div>
                    <div>
                        <strong>Quantity</strong>
                    </div>
                    <div>
                        <strong>Price</strong>
                    </div>
                </CartListTitle>

            </CartItems>
            <CartSummary>
                <div>
                    Total Number of Items:
                            </div>
                <div>
                    <strong>X</strong>
                </div>
                <div>
                    Total Price:
                            </div>
                <div>
                    <strong>Y</strong>
                </div>
            </CartSummary>
        </CartSection>
    )
}


const CartSection = styled.div`
padding:20px;
    width: 300px;
    height: auto;
    background: #fff ;
`;

const CartHeader = styled.div`
    text-align: center;
    padding-top: 30px;
    padding-bottom: 30px;
    border-bottom: 1px solid black;

`;

const CartHeading = styled.h1`
    font-weight: 800:
`;

const CartItems = styled.ul`
    list-style-type: none;
    padding-bottom: 20px;
    border-bottom: 1px solid black;
`;

const CartListItems = styled.li`
    padding-bottom: 10px;
    display: grid;
    grid-template-columns: 50% 30% auto;

`;

const CartListTitle = styled.div`
    padding: 20px 0;
    display: grid;
    grid-template-columns: 50% 30% auto;
`;

const CartSummary = styled.div`
    display: grid;
    grid-template-columns: 80% 20%;
    grid-gap: 20px;
    padding-top: 20px;
`;

export default Cart;
