import React, { useState, useEffect, } from 'react';
import styled from 'styled-components';
import { CartProvider, useCart } from "react-use-cart";
import axios from 'axios';
import Navbar from './components/Navbar';
import SearchBar from './components/SearchBar';

function Home() {
  let loadedNum = 0;
  const { addItem } = useCart();
  let [data, setData] = useState([]);

  // Fetching data from the API
  useEffect(() => {
    axios('http://localhost:8000/api/robots/')
      .then(response => {
        setData(response.data.data);
      })
      .catch(error => {
        console.log('Error Fetching Data from the API ' + error);
      })

  }, [])

  // Adding Unique product identifier
  data.forEach((obj, i) => obj.id = "SKU" + i)


  return (
    <ProductsWrapper>
      <SearchBar />
      <ProductListUL>
        {data.map((item) => (
          <ProductList key={item.id}>
            <Card>
              <Photo src={item.image} alt={item.name} />
              <Name>{item.name}</Name>
              <ItemInfo>
                Price:<Price> {Intl.NumberFormat('th-TH', { style: 'currency', currency: 'THB' }).format(item.price)}</Price>
              In-Stock:<Stock> {item.stock}</Stock>
              Created Date:<Date> {item.createdAt.substr(0, 9).split('-').reverse().join('-')}</Date>
              Material:<Material> {item.material}</Material>
                {/* <IDSet>{loadedNum = item.id}</IDSet> */}
              </ItemInfo>
              <AddToCart disabled={item.stock === 0} onClick={() => addItem(item)} >Add to Cart</AddToCart>
            </Card>
          </ProductList>
        ))}
      </ProductListUL>
      <ShowMore>
        <ShowMoreButton disabled={loadedNum === data.length} onClick={loadMore}>Load more</ShowMoreButton>
      </ShowMore>
    </ProductsWrapper>
  );
}

function Cart() {
  const {
    isEmpty,
    totalItems,
    items,
    cartTotal,
    updateItemQuantity,
    removeItem,
  } = useCart();

  if (isEmpty) return (
    <CartWrapper>
      <CartHeader>
        <CartHeading>Cart ({totalItems})</CartHeading>
        <CartMessage>Your cart is empty</CartMessage>
      </CartHeader>
      <CartFooter>
        <p>Total Number of Items: </p>
        <p>Total Amount: </p>
      </CartFooter>
    </CartWrapper>);

  else return (
    <CartWrapper>
      <CartHeading>Cart ({totalItems})</CartHeading>
      <CartUL>
        {items.map((item) => (
          <CartLI key={item.id}>
            {item.quantity} x {item.name} &mdash;
            <button
              onClick={() => updateItemQuantity(item.id, item.quantity - 1)}
            >
              -
            </button>
            <button
              onClick={() => updateItemQuantity(item.id, item.quantity + 1)}
            >
              +
            </button>
            <button onClick={() => removeItem(item.id)}>&times;</button>
          </CartLI>
        ))}
      </CartUL>
      <CartFooter>
        <p>Total Number of Items: {totalItems}</p>
        <p>Total Amount: {cartTotal.toFixed(2)}</p>
      </CartFooter>
    </CartWrapper>
  );
}

function App() {
  return (
    <CartProvider>
      <Navbar />
      <Container>
        <Home />
        <Cart />
      </Container>
    </CartProvider>
  );
}

const Container = styled.div`
  display: grid;
  grid-template-columns: 80% 20%;
  color: #000;
  height: 100%;
  width: 100%;
`;

const ProductsWrapper = styled.div`
  border-right: 1px solid #D5D8DC ;
  padding-bottom: 30px;
  padding-top: 30px;
`;


const Card = styled.div`
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
    transition: 0.3s;
    width: 300px;
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 20px;
    padding-bottom: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
    }
`;
const Photo = styled.img`
    height: 150px;
    margin-bottom: 10px;
`;
const Name = styled.h3`
    margin-bottom: 15px;
    font-size:18px;
`;
const ItemInfo = styled.div`
    width: 90%;
    padding: 5px 15px;
    display: grid;
    grid-template-columns: auto auto;

`;
const Price = styled.h4`
    color: green;
    font-size:14px;
`;
const Stock = styled.h4`
    font-size:14px;
`;
const Date = styled.h4`
    font-size:14px;
`;
const Material = styled.h4`
    font-size:14px;
`;

const AddToCart = styled.button`
    background: #ff0080;
    width: 90%;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }

    &:disabled {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0);
        background: #ABB2B9 ;
    }
`;

const ShowMore = styled.div`
    padding: 30px 5px;

`;

const ShowMoreButton = styled.button`
    background: #ff0080;
    width: 100%;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }

    &:disabled {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0);
        background: #ABB2B9 ;
    }
`;

const CartWrapper = styled.div`
    padding-top: 50px;
    padding-left: 20px;
    padding-right: 20px;
    text-align: center;
`;

const CartHeader = styled.div`

`;
const CartHeading = styled.h1`
    padding-bottom: 20px;
    border-bottom: 2px solid #D5D8DC;
`;
const CartMessage = styled.p`
    padding-bottom: 20px;
    padding-top: 20px;
    border-bottom: 2px solid #D5D8DC;
`;
const CartFooter = styled.div`
  border-bottom: 2px solid #D5D8DC;
`;

const ProductListUL = styled.ul`
  margin-top: 40px;
  display: flex;
  justify-content: space-evenly;
  flex-wrap: wrap;
  gap: 25px;
  list-style-type: none;
`;

const CartUL = styled.ul`
  list-style-type: none;
  display: flex;
  flex-direction: column;
  text-align: left;
`;

const CartLI = styled.li`
  padding-top: 10px;
  padding-bottom: 10px;
  border-bottom: 0.5px solid #D5D8DC;
`;

const ProductList = styled.li`

`;

export default App;
