import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import Navbar from './Navbar';

const Home = () => {
    const [data, setData] = useState([]);


    useEffect(() => {
        axios('http://localhost:8000/api/robots/')
            .then(response => {
                setData(response.data.data);
            })
            .catch(error => {
                console.log('Error getting fake data: ' + error);
            })
    }, [])

    return (
        <MainContainer>
            <Navbar />
            <ProductContainer>
                <ProductsSection>
                    <Search>
                        <h2>New Arrivals</h2>
                        <SearchInput type="text" placeholder="Find your robots" />
                    </Search>
                    <ul>
                        {data.map((item) => {
                            return <li key={Math.random().toString(36).substr(2, 9)}>
                                <Card>
                                    <Photo src={item.image} alt={item.name} />
                                    <Name>{item.name}</Name>
                                    <ItemInfo>
                                        Price:<Price> {Intl.NumberFormat('th-TH', { style: 'currency', currency: 'THB' }).format(item.price)}</Price>
                                    In-Stock:<Stock> {item.stock}</Stock>
                                    Created Date:<Date> {item.createdAt.substr(0, 9).split('-').reverse().join('-')}</Date>
                                    Material:<Material> {item.material}</Material>
                                    </ItemInfo>
                                    <AddToCart disabled={item.stock === 0}>Add to Cart</AddToCart>
                                </Card></li>
                        })}
                    </ul>

                </ProductsSection>

                <CartSection>

                </CartSection>

            </ProductContainer>

        </MainContainer>

    )
}
const MainContainer = styled.div`
    color: #fff;
    height: 100vh;
    width: 100%;
`;

const Search = styled.div`
    display: grid;
    grid-template-columns: 20% 80%;
    justify-content: center;
    align-items: center;
    padding-right: 18px;
`;

const SearchInput = styled.input`
    padding: 0.8rem 1.2rem;
    border-radius: 20px;
    outline: none;
`;


const Photo = styled.img`
    height: 150px;
    margin-bottom: 10px;
`;


const ProductContainer = styled.div`
    color: #000;
    display: flex;
    justify-content: space-between;
    background: #fff;
    height: 100%;
`;

const ProductsSection = styled.div`
    width: 80%;
    height: 100vh;
    background: #fff ;
    padding: 30px 20px;

    ul {
        margin-top: 40px;
        display: flex;
        justify-content: space-around;
        flex-wrap: wrap;
        gap: 25px;
        list-style-type: none;
    }
    h2{
        padding-left: 20px;
    }
`;

const Card = styled.div`
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
    transition: 0.3s;
    width: 300px;
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 20px;
    padding-bottom: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
    }
`;

const ItemInfo = styled.div`
    width: 90%;
    padding: 5px 15px;
    display: grid;
    grid-template-columns: auto auto;
`;

const Name = styled.h3`
    margin-bottom: 15px;
`;

const Price = styled.h4``;
const Stock = styled.h4``;
const Date = styled.h4``;
const Material = styled.h4``;

const AddToCart = styled.button`
    background: #ff0080;
    width: 90%;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }

    &:disabled {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0);
        background: #ABB2B9 ;
    }
`;


const CartSection = styled.div`
    width: 20%;
    height: 100vh;
    background: #F8F9F9 ;
    border-left: 1px solid #D5D8DC ;
`;


export default Home;
