import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Home = () => {
    const [data, setData] = useState([]);

    useEffect(() => {
        fetch('http://localhost:8000/api/robots/')
            .then((response) => response.json())
            .then((data) => setData(data.data.splice(0, 10)))

    }, [])

    return (
        <ProductContainer>
            <ProductsSection>
                <h2>Trending Robots</h2>
                <ul>
                    {data.map((item) => {
                        return <li key={Math.random().toString(36).substr(2, 9)}>
                            <Card>
                                <img src={item.image} alt={item.name} />
                                <h3>{item.name}</h3>
                                <h4>{item.price}</h4>
                                <h4>{item.stock}</h4>
                                <h4>{item.createdAt}</h4>
                                <h4>{item.material}</h4>
                            </Card></li>
                    })}
                </ul>

            </ProductsSection>

            <CartSection>

            </CartSection>

        </ProductContainer>
    )
}

const ProductContainer = styled.div`
    color: #000;
    display: flex;
    justify-content: space-between;
    background: #fff;
    height: 100vh;
    padding: 1rem 1rem;
`;

const ProductsSection = styled.div`
    width: 80%;
    height: 100vh;
    background: #fff ;

    ul {
        display: flex;
        justify-content: space-around;
        flex-wrap: wrap;
        list-style-type: none;
    }
`;

const Card = styled.div`
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
    transition: 0.3s;
    width: 300px;
    height: 300px;

    &:hover {

    }
`;

const CartSection = styled.div`
    width: 20%;
    height: 100vh;
    background: #F8F9F9 ;
    border-left: 1px solid #D5D8DC ;
`;


export default Home;
