import React from 'react';
import { useCart } from "use-cart";

const Cart = ({ data }) => {
    const { clearCart, items, lineItemsCount } = useCart();

    const getItemData = sku => {
        return data.find(d => d.sku === sku);
      };

      const CartLineItem = ({ item }) => {
        const { addItem, removeItem, removeLineItem } = useCart();
        const productData = getItemData(item.sku);
        return (
          <div>
            {productData.name} <button onClick={() => removeItem(item.sku)}>-</button>
            {item.quantity} @ ${productData.price}
            <button onClick={() => addItem(item.sku)}>+</button>
            <button onClick={() => removeLineItem(item.sku)}>x</button>
          </div>
        );
      };

      const Cart = () => {


        return (
          <div>
            <h1>Cart: {lineItemsCount}</h1>
            {items.map(item => (
              <CartLineItem key={item.sku} item={item} />
            ))}
            <hr />
            <button onClick={clearCart}>Empty cart</button>
            <button onClick={() => console.log(items)}>Checkout</button>
          </div>
        );
      };
}

export default Cart;
