import React, { useState, useEffect, } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import Navbar from './Navbar';
import SearchBar from './SearchBar';

const Home = () => {
    const limit = 12;
    const [data, setData] = useState([]);
    const [visibleItems, setVisibleItems] = useState(limit);

    useEffect(() => {
        axios('http://localhost:8000/api/robots/')
            .then(response => {
                setData(response.data.data);
            })
            .catch(error => {
                console.log('Error Fetching Data from the API ' + error);
            })

    }, [])

    const loadMore = () => {
        setVisibleItems(previousVisibleItems => previousVisibleItems + 12)
    }

    return (
        <MainContainer>
            <Navbar />
            <ProductContainer>
                <ProductsSection>
                    <SearchBar />
                    <RobotItems>
                        {data.slice(0, visibleItems).map((item) => {
                            return <li key={Math.random().toString(36).substr(2, 9)}>
                                <Card>
                                    <Photo src={item.image} alt={item.name} />
                                    <Name>{item.name}</Name>
                                    <ItemInfo>
                                        Price:<Price> {Intl.NumberFormat('th-TH', { style: 'currency', currency: 'THB' }).format(item.price)}</Price>
                                        In-Stock:<Stock> {item.stock}</Stock>
                                        Created Date:<Date> {item.createdAt.substr(0, 9).bind.split('-').reverse().join('-')}</Date>
                                        Material:<Material> {item.material}</Material>
                                    </ItemInfo>
                                    <AddToCart disabled={item.stock === 0} onClick={addToCart}>Add to Cart</AddToCart>
                                </Card></li>
                        })}
                    </RobotItems>
                    <ShowMore>
                        <ShowMoreButton onClick={loadMore}>Load more</ShowMoreButton>
                    </ShowMore>
                </ProductsSection>

                <CartSection>

                </CartSection>
            </ProductContainer>
        </MainContainer>

    )
}
const MainContainer = styled.div`
    color: #fff;
    height: 100%;
    width: 100%;
`;

const Photo = styled.img`
    height: 150px;
    margin-bottom: 10px;
`;


const ProductContainer = styled.div`
    color: #000;
    display: flex;
    justify-content: space-between;
    background: #fff;
    height: auto;
`;

const ProductsSection = styled.div`
    border-right: 1px solid #D5D8DC;  
    width: 80%;
    height: auto;
    background: #fff ;
    padding: 30px 20px;


    h2{
        padding-left: 20px;
        font-weight:900;
    }
`;

const RobotItems = styled.ul`
    margin-top: 40px;
    display: flex;
    justify-content: space-around;
    flex-wrap: wrap;
    gap: 25px;
    list-style-type: none;
`;


const Card = styled.div`
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
    transition: 0.3s;
    width: 300px;
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 20px;
    padding-bottom: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
    }
`;

const ItemInfo = styled.div`
    width: 90%;
    padding: 5px 15px;
    display: grid;
    grid-template-columns: auto auto;
`;

const Name = styled.h3`
    margin-bottom: 15px;
`;

const Price = styled.h4``;
const Stock = styled.h4``;
const Date = styled.h4``;
const Material = styled.h4``;

const AddToCart = styled.button`
    background: #ff0080;
    width: 90%;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }

    &:disabled {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0);
        background: #ABB2B9 ;
    }
`;

const ShowMore = styled.div`
    padding: 30px 5px;

`;

const ShowMoreButton = styled.button`
    background: #ff0080;
    width: 100%;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }

    &:disabled {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0);
        background: #ABB2B9 ;
    }
`;


const CartSection = styled.div`
    width: 300px;
    height: auto;
    background: #fff ;
`;



export default Home;
