import React, { useState, useEffect, } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { CartProvider } from 'use-cart';
import Item from './components/Item';
import Cart from './components/Cart';

function App() {
  let [data, setData] = useState([]);

  useEffect(() => {
    axios('http://localhost:8000/api/robots/')
      .then(response => {
        setData(response.data.data);
      })
      .catch(error => {
        console.log('Error Fetching Data from the API ' + error);
      })

  }, [])

  const addSKU = () => {
    data.forEach((obj, i) => obj.sku = "sku-" + i)
  }

  addSKU();


  console.log(data);

  return (
    <CartProvider>
      <div className="App">
        <div className="store">
          <h1>Your internet shop</h1>
          {data.map(d => (
            <Item key={d.sku} name={d.name} image={d.image} price={d.price} stock={d.sku} createdAt={d.createdAt} material={d.material} />
          ))}
        </div>
        <div className="cart">
          <Cart data={data} />
        </div>
      </div>
    </CartProvider>
  );
}

export default App;
