import React, { useState, useEffect, } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import Navbar from './Navbar';
import SearchBar from './SearchBar';

const Home = () => {
    const limit = 12;
    const [data, setData] = useState([]);
    const [visibleItems, setVisibleItems] = useState(limit);
    const [searchValue, setSearchValue] = useState("");
    const [inCart, setInCart] = useState([]);

    // const inCart = [];
    let loadedNum = 0;
    let itemName = "";
    let itemPrice = 0.0;

    useEffect(() => {
        axios('http://localhost:8000/api/robots/')
            .then(response => {
                setData(response.data.data);
            })
            .catch(error => {
                console.log('Error Fetching Data from the API ' + error);
            })

    }, [])

    data.forEach((obj, i) => obj.id = i + 1)

    const filterRobots = ({ material }) => {
        return material.toLowerCase().indexOf(searchValue.toLowerCase()) !== -1;
    };

    const loadMore = () => {
        setVisibleItems(previousVisibleItems => previousVisibleItems + 12)
    }

    let addToCart = (e) => {
        inCart.push(data[e - 1]);
        setInCart(inCart);
        console.log(inCart);
        inCart.map((anItem) => {
            return <li key={anItem.id}>
                    {itemName = anItem.name}
                    {itemName = anItem.name}
                    {itemPrice = anItem.price}
                    <div>
                        {anItem.name}
                    </div>
                    <div>
                        {10}
                    </div>
                    <div>
                        {anItem.price}
                    </div>

            </li>
        })
    }

    return (
        <MainContainer>
            <Navbar />
            <ProductContainer>
                <ProductsSection>
                    <SearchBar onSearch={setSearchValue} value={searchValue} />
                    <RobotItems>
                        {data.slice(0, visibleItems).filter(filterRobots).map((item) => {
                            return <li key={item.id}>
                                { }
                                <Card>
                                    <Photo src={item.image} alt={item.name} />
                                    <Name>{item.name}</Name>
                                    <ItemInfo>
                                        Price:<Price> {Intl.NumberFormat('th-TH', { style: 'currency', currency: 'THB' }).format(item.price)}</Price>
                                        In-Stock:<Stock> {item.stock}</Stock>
                                        Created Date:<Date> {item.createdAt.substr(0, 9).split('-').reverse().join('-')}</Date>
                                        Material:<Material> {item.material}</Material>
                                        <IDSet>{loadedNum = item.id}</IDSet>
                                    </ItemInfo>
                                    <AddToCart disabled={item.stock === 0} onClick={() => addToCart(item.id)} >Add to Cart</AddToCart>
                                </Card></li>
                        })}
                    </RobotItems>
                    <ShowMore>
                        <ShowMoreButton disabled={loadedNum === data.length} onClick={loadMore}>Load more</ShowMoreButton>
                    </ShowMore>
                </ProductsSection>
                <CartSection>
                    <CartHeader>
                        <CartHeading>YOUR CART</CartHeading>
                    </CartHeader>
                    <CartItems>
                        <CartListTitle>
                            <div>
                                <strong>Name</strong>
                            </div>
                            <div>
                                <strong>Quantity</strong>
                            </div>
                            <div>
                                <strong>Price</strong>
                            </div>
                        </CartListTitle>
                        {inCart.map((anItem) => {
                            return <li key={anItem.id}>
                                <CartListItems>
                                    <div>
                                        {anItem.name}
                                    </div>
                                    <div>
                                        {10}
                                    </div>
                                    <div>
                                        {anItem.price}
                                    </div>
                                </CartListItems>
                            </li>
                        })}

                    </CartItems>
                    <CartSummary>
                        <div>
                            Total Number of Items:
                            </div>
                        <div>
                            <strong>X</strong>
                        </div>
                        <div>
                            Total Price:
                            </div>
                        <div>
                            <strong>Y</strong>
                        </div>
                    </CartSummary>
                </CartSection>
            </ProductContainer>
        </MainContainer>
    )
}
const MainContainer = styled.div`
    color: #fff;
    height: 100%;
    width: 100%;
`;

const Photo = styled.img`
    height: 150px;
    margin-bottom: 10px;
`;


const ProductContainer = styled.div`
    color: #000;
    display: flex;
    justify-content: space-between;
    background: #fff;
    height: auto;
`;

