    background: #fff ;
    padding: 30px 20px;

    h2{
        padding-left: 20px;
        font-weight:900;
    }
`;

const RobotItems = styled.ul`
    margin-top: 40px;
    display: flex;
    justify-content: space-around;
    flex-wrap: wrap;
    gap: 25px;
    list-style-type: none;
`;


const Card = styled.div`
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
    transition: 0.3s;
    width: 300px;
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 20px;
