    background: #ff0080;
    width: 90%;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }

    &:disabled {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0);
        background: #ABB2B9 ;
    }
`;

const ShowMore = styled.div`
    padding: 30px 5px;

`;

const ShowMoreButton = styled.button`
    background: #ff0080;
    width: 100%;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }

    &:disabled {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0);
        background: #ABB2B9 ;
    }
`;


const CartSection = styled.div`
padding:20px;
    width: 300px;
    height: auto;
    background: #fff ;
`;

const CartHeader = styled.div`
    text-align: center;
    padding-top: 30px;
    padding-bottom: 30px;
    border-bottom: 1px solid black;

`;

const CartHeading = styled.h1`
    font-weight: 800:
`;

const CartItems = styled.ul`
    list-style-type: none;
    padding-bottom: 20px;
    border-bottom: 1px solid black;
`;

const CartListItems = styled.li`
    padding-bottom: 10px;
    display: grid;
    grid-template-columns: 50% 30% auto;

`;

const CartListTitle = styled.div`
    padding: 20px 0;
    display: grid;
    grid-template-columns: 50% 30% auto;
