import React, { useState, useEffect, } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import Navbar from './Navbar';
import SearchBar from './SearchBar';

const Home = () => {
    const limit = 12;
    const [data, setData] = useState([]);
    const [visibleItems, setVisibleItems] = useState(limit);
    const [searchValue, setSearchValue] = useState("");
    let loadedNum = 0;
    let inCart = [];

    useEffect(() => {
        axios('http://localhost:8000/api/robots/')
            .then(response => {
                setData(response.data.data);
            })
            .catch(error => {
                console.log('Error Fetching Data from the API ' + error);
            })

    }, [])

    data.forEach((obj, i) => obj.id = i + 1)

    const filterRobots = ({ material }) => {
        return material.toLowerCase().indexOf(searchValue.toLowerCase()) !== -1;
    };

    const loadMore = () => {
        setVisibleItems(previousVisibleItems => previousVisibleItems + 12)
    }

    let addToCart = (e) => {
        console.log(e);
        inCart = data[e];
        console.log(inCart);
    }

    return (
        <MainContainer>
            <Navbar />
            <ProductContainer>
                <ProductsSection>
                    <SearchBar onSearch={setSearchValue} value={searchValue} />
                    <RobotItems>
                        {data.slice(0, visibleItems).filter(filterRobots).map((item) => {
                            return <li key={item.id}>
                                { }
                                <Card>
                                    <Photo src={item.image} alt={item.name} />
                                    <Name>{item.name}</Name>
                                    <ItemInfo>
                                        Price:<Price> {Intl.NumberFormat('th-TH', { style: 'currency', currency: 'THB' }).format(item.price)}</Price>
                                        In-Stock:<Stock> {item.stock}</Stock>
                                        Created Date:<Date> {item.createdAt.substr(0, 9).split('-').reverse().join('-')}</Date>
                                        Material:<Material> {item.material}</Material>
                                        {loadedNum = item.id}
                                    </ItemInfo>
                                    <AddToCart disabled={item.stock === 0} onClick={() => addToCart(item.id)} >Add to Cart</AddToCart>
                                </Card></li>
                        })}
                    </RobotItems>
                    <ShowMore>
                        <ShowMoreButton disabled={loadedNum === data.length} onClick={loadMore}>Load more</ShowMoreButton>
                    </ShowMore>
                </ProductsSection>

                <CartSection>
                    <CartHeader>
                        <CartHeading>YOUR CART</CartHeading>
                    </CartHeader>
                    <CartItems>
                        <CartListTitle>
                            <div>
                                <strong>Name</strong>
                            </div>
                            <div>
                                <strong>Quantity</strong>
                            </div>
                            <div>
                                <strong>Price</strong>
                            </div>
                        </CartListTitle>
                        {/* {cartData.slice(0, 5).map((anItem) => {
                            return <li key={Math.random().toString(36).substr(2, 9)}>
                                <CartListItems>
                                    <div>
                                        {anItem.name}
                                    </div>
                                    <div>
                                        {newCount++}
                                    </div>
                                    <div>
                                        {anItem.price}
                                    </div>
                                </CartListItems>
                            </li>
                        })} */}
                    </CartItems>
                    <CartSummary>
                        <div>
                            Total Number of Items:
                            </div>
                        <div>
                            <strong>X</strong>
                        </div>
                        <div>
                            Total Price:
                            </div>
                        <div>
                            <strong>Y</strong>
                        </div>
                    </CartSummary>
                </CartSection>
            </ProductContainer>
        </MainContainer>

    )
}
const MainContainer = styled.div`
    color: #fff;
    height: 100%;
    width: 100%;
`;

const Photo = styled.img`
    height: 150px;
    margin-bottom: 10px;
`;


const ProductContainer = styled.div`
    color: #000;
    display: flex;
    justify-content: space-between;
    background: #fff;
    height: auto;
`;

const ProductsSection = styled.div`
    border-right: 1px solid #D5D8DC;  
    width: 80%;
    height: auto;
    background: #fff ;
    padding: 30px 20px;

    h2{
        padding-left: 20px;
        font-weight:900;
    }
`;

const RobotItems = styled.ul`
    margin-top: 40px;
    display: flex;
    justify-content: space-around;
    flex-wrap: wrap;
    gap: 25px;
    list-style-type: none;
`;


const Card = styled.div`
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
    transition: 0.3s;
    width: 300px;
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 20px;
    padding-bottom: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
    }
`;

const ItemInfo = styled.div`
    width: 90%;
    padding: 5px 15px;
    display: grid;
    grid-template-columns: auto auto;
`;

const Name = styled.h3`
    margin-bottom: 15px;
`;

const Price = styled.h4``;
const Stock = styled.h4``;
const Date = styled.h4``;
const Material = styled.h4``;

const AddToCart = styled.button`
    background: #ff0080;
    width: 90%;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }

    &:disabled {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0);
        background: #ABB2B9 ;
    }
`;

const ShowMore = styled.div`
    padding: 30px 5px;

`;

const ShowMoreButton = styled.button`
    background: #ff0080;
    width: 100%;
    height: 50px;
    border: none;
    margin-top: 20px;

    &:hover {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0.4);
    }

    &:disabled {
        box-shadow: 0 8px 16px 0 rgba(0,0,0,0);
        background: #ABB2B9 ;
    }
`;


const CartSection = styled.div`
padding:20px;
    width: 300px;
    height: auto;
    background: #fff ;
`;

const CartHeader = styled.div`
    text-align: center;
    padding-top: 30px;
    padding-bottom: 30px;
    border-bottom: 1px solid black;

`;

const CartHeading = styled.h1`
    font-weight: 800:
`;

const CartItems = styled.ul`
    list-style-type: none;
    padding-bottom: 20px;
    border-bottom: 1px solid black;
`;

const CartListItems = styled.li`
    padding-bottom: 10px;
    display: grid;
    grid-template-columns: 50% 30% auto;

`;

const CartListTitle = styled.div`
    padding: 20px 0;
    display: grid;
    grid-template-columns: 50% 30% auto;
`;

const CartSummary = styled.div`
    display: grid;
    grid-template-columns: 80% 20%;
    grid-gap: 20px;
    padding-top: 20px;
`;

export default Home;
